$(function(){$.fn.twister=function(options){options=jQuery.extend({duration:250,degrees:5,scale:'120%',overflow:'hidden'},options);$(this).each(function(){var container=$(this);$(this).find('img').load(function(){$(container).css({height:$(this).height(),width:$(this).width(),overflow:options.overflow});});}).mouseenter(function(){$(this).find('img').css({zIndex:'9999'}).animate({marginTop:(options.overflow=='hidden'?'-'+options.degrees+'%':'0'),marginLeft:(options.overflow=='hidden'?'-'+options.degrees+'%':'0'),rotate:options.degrees+'deg',width:options.scale},{duration:options.duration,queue:true});}).mouseleave(function(){$(this).find('img').css({zIndex:'9988'}).animate({marginTop:'0',marginLeft:'0',rotate:'0deg',width:'100%'},{duration:options.duration,queue:true});});}});